                                                                                                                                                                                                                                                                                                                     package cn.itcast.arraylist.test;

import java.util.ArrayList;
import java.util.Iterator;

import cn.itcast.bean.Person;

public class ArrayListTest2 {

	public static void main(String[] args) {
		
		
	//singleDemo();
		
		ArrayList a1 = new ArrayList();
		a1.add(new Person("lisi1",21));
		a1.add(new Person("lisi2",22));
		a1.add(new Person("lisi3",23));
		a1.add(new Person("lisi3",23));
		a1.add(new Person("lisi4",24));
		a1.add(new Person("lisi4",24));
		System.out.println(a1);
		
		a1 = getSingleElement(a1);
		
		System.out.println(a1);
		System.out.println(a1.remove(new Person("lisi2",22)));
		System.out.println(a1);
	}

	


	/**
	 * 
	 */
	private static void singleDemo() {
		ArrayList a1 = new ArrayList();
		a1.add("abc1");
		a1.add("abc2");
		a1.add("abc2");
		a1.add("abc1");
		a1.add("abc");
		System.out.println(a1);
		a1 = getSingleElement(a1);
		System.out.println(a1);
	}

	public static ArrayList getSingleElement(ArrayList a1) {
		//1.定义一个临时容器。
	ArrayList temp = new ArrayList();
	   //2.迭代a1集合。
	Iterator it = a1.iterator();
	while(it.hasNext()){
		Object obj = it.next();
		//3.判断被迭代的元素是否在临时容器中存在。
		if(!temp.contains(obj)){
			temp.add(obj);
		}
	}
	
		return temp;
	}

}
